# day 2 puzzle 1
def coord2num(x_y):
    # translate coordinates to a number
    # 1 2 3   -1,1  0,1  1,1
    # 4 5 6   -1,0  0,0  1,0
    # 7 8 9  -1,-1 0,-1  1,-1
    xy2num = {(-1,1): '1', (0,1): '2', (1,1): '3', (-1,0): '4', (0,0): '5', (1,0): '6', (-1,-1): '7', (0,-1): '8', (1,-1): '9'}
    return xy2num[x_y]
def move(x_y, direction):
    # change coordinates according to direction given
    if direction == 'U' and x_y[1] < 1:
        x_y[1] += 1
    elif direction == 'D' and x_y[1] > -1:
        x_y[1] -= 1
    elif direction == 'R' and x_y[0] < 1:
        x_y[0] += 1
    elif direction == 'L' and x_y[0] > -1:
        x_y[0] -= 1

x_y = [0,0]  # starting coordinates (mutable object)
input = """



"""
buttons = []  # track final button at the end of each line
for line in input.splitlines():
    for direction in line:
        move(x_y, direction)
    buttons.append(coord2num((x_y[0], x_y[1])))

print(''.join(buttons))
