# day 2 puzzle 2
def coord2num(x, y):
    # translate coordinates to a number
    #     1                    0,2
    #   2 3 4           -1,1   0,1   1,1
    # 5 6 7 8 9  -2,0   -1,0   0,0   1,0   2,0
    #   A B C           -1,-1  0,-1  1,-1
    #     D                    0,-2
    xy2num = {(0,2):'1',(-1,1):'2',(0,1):'3',(1,1):'4',(-2,0):'5',(-1,0):'6',(0,0):'7',(1,0):'8',(2,0):'9',
              (-1,-1):'A',(0,-1):'B',(1,-1):'C',(0,-2):'D'}
    return xy2num[(x, y)]
def move(x, y, direction):
    # change coordinates according to direction given
    if   direction == 'U' and (y<0 or (abs(x) + abs(y)) < 2):
        y += 1
    elif direction == 'D' and (y>0 or (abs(x) + abs(y)) < 2):
        y -= 1
    elif direction == 'R' and (x<0 or (abs(x) + abs(y)) < 2):
        x += 1
    elif direction == 'L' and (x>0 or (abs(x) + abs(y)) < 2):
        x -= 1
    return x, y

x, y = -2, 0
input = """



"""
buttons = []  # track final button at the end of each line
for line in input.splitlines():
    for direction in line:
        x, y = move(x, y, direction)
    button = coord2num(x, y)
    buttons.append(button)
    print('button: ', button)

print(''.join(buttons))
